import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";

const list = [
  { rank: 1,
    score:"",
    professor: "aaa",
    homepage: "aa"},
  { rank: 2,
    score:"",
    professor: "bb",
    homepage: "bb"}
]

const proxyurl = "https://cors-anywhere.herokuapp.com/";
const backendurl = "https://backend.cs411team1.web.illinois.edu/polls/";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resultList: [],
      modal: false,
      Q:"",
      activeItem: {
        query: "",
        rank:"",
        score:0.0,
        professor: "",
        homepage: "",
      },
    };
  }


  toggle = () => {
    this.setState({ modal: !this.state.modal });
  };

  handleSubmit = (item) => {
    this.toggle();
    var query = item.query
    query = query.replaceAll(" ","_")
    this.setState({ Q: item.query})
    console.log(item.query)
    axios
        .get(proxyurl+backendurl+"getProfessors/"+`${query}`)
        .then(res => this.getProfessorUpdate(res.data, item));
  };
  getProfessorUpdate(data, item){

    var templist = []
    var temp = { rank: "", score:"",professor: "", homepage: "" };
    for (var i=0;i<data.length;i++) {
      temp = JSON.parse(data[i]);
      templist.push(temp)
    }
    this.setState({ resultList: templist })
  }

  createItem = () => {
    const item = { rank: "", score:"",professor: "", homepage: "" };
    this.setState({ activeItem: item, modal: !this.state.modal });
  };
  createItem2 = () => {
    const item = { professor: "", homepage: "" };
    this.setState({ activeItem: item, modal: !this.state.modal });
  };

  renderItems = () => {
    const newItems = this.state.resultList
    console.log(newItems)
    return newItems.map((item) => (
        <li
            key={item.rank}
            className="list-group-item d-flex justify-content-between align-items-center"
        >
        <span
            className={`todo-title mr-2`}
            title={item.homepage}
        >
          <div className="d"><a href={item.homepage}>{item.professor}</a></div>
          <div>rank: {item.rank}, score: {item.score}</div>
        </span>
        </li>
    ));
  };

  render() {
    return (
        <main className="container">
          <h1 className="text-black-50 text-uppercase text-center my-4" >Professor Search Engine</h1>
          <div className="row">
            <div className="col-md-6 col-sm-10 mx-auto p-0">
              <div className="card p-3">
                <div className="d"><a href={proxyurl}>{proxyurl}</a></div>
                <div className="mb-4">
                  <button
                      className="btn btn-primary"
                      onClick={this.createItem}
                  >
                    Rank Professor
                  </button>
                  {/*&nbsp;*/}
                  {/*<button*/}
                  {/*    className="btn btn-primary"*/}
                  {/*    onClick={this.createItem2}*/}
                  {/*>*/}
                  {/*  Rank Paper*/}
                  {/*</button>*/}
                </div>
                <div>Current Query: {this.state.Q}</div>
                {/*{this.renderTabList()}*/}
                <ul className="list-group list-group-flush border-top-0">
                  {this.renderItems()}
                </ul>
              </div>
            </div>
          </div>
          {this.state.modal ? (
              <Modal
                  activeItem={this.state.activeItem}
                  toggle={this.toggle}
                  onSave={this.handleSubmit}
              />
          ) : null}
        </main>
    );
  }
}

export default App;
