import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";

const list = [
  { rank: 1,
    score:"",
    professor: "aaa",
    homepage: "aa"},
  { rank: 2,
    score:"",
    professor: "bb",
    homepage: "bb"}
]

const proxyurl = "https://cors-anywhere.herokuapp.com/";
const backendurl = "https://backend.cs411team1.web.illinois.edu/polls/";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resultList: [],
      modal: false,
      activeItem: {
        query: "",
        professor: "",
        homepage: "",
      },
    };
  }

  componentDidMount() {
    this.refreshList();
  }

  refreshList = () => {
    axios
        .get("/api/todos/")
        .then((res) => this.setState({ resultList: res.data }))
        .catch((err) => console.log(err));
  };

  toggle = () => {
    this.setState({ modal: !this.state.modal });
  };

  handleSubmit = (item) => {
    this.toggle();
    var query = item.query
    item.query = query.replaceAll(" ","%20")
    console.log(item.query)
    axios
        .get(proxyurl+backendurl+"getProfessors/"+`${item.query}`)
        .then(res => this.getProfessorUpdate(res.data, item));
    // if (item.id) {
    //   axios
    //       .put(`/api/todos/${item.id}/`, item)
    //       .then((res) => this.refreshList());
    //   return;
    // }
    // axios
    //     .post("/api/todos/", item)
    //     .then((res) => this.refreshList());
  };
  getProfessorUpdate(data, item){
    console.log(data)
    data = JSON.parse(JSON.stringify(data));
    this.setState({ resultList: data })
  }




  // handleDelete = (item) => {
  //   axios
  //       .delete(`/api/todos/${item.id}/`)
  //       .then((res) => this.refreshList());
  // };

  createItem = () => {
    const item = { professor: "", homepage: "" };
    this.setState({ activeItem: item, modal: !this.state.modal });
  };
  createItem2 = () => {
    const item = { professor: "", homepage: "" };
    this.setState({ activeItem: item, modal: !this.state.modal });
  };

  renderItems = () => {
    const newItems = this.state.resultList
    return newItems.map((item) => (
        <li
            key={item.rank}
            className="list-group-item d-flex justify-content-between align-items-center"
        >
        <span
            className={`todo-title mr-2`}
            title={item.homepage}
        >
          <div className="d"><a href={item.homepage}>{item.professor}</a></div>
          <div>rank:{item.rank},score:{item.score}</div>
        </span>
        </li>
    ));
  };

  render() {
    return (
        <main className="container">
          <h1 className="text-black-50 text-uppercase text-center my-4" >Professor Search Engine</h1>
          <div className="row">
            <div className="col-md-6 col-sm-10 mx-auto p-0">
              <div className="card p-3">
                <div className="mb-4">
                  <button
                      className="btn btn-primary"
                      onClick={this.createItem}
                  >
                    Rank Professor
                  </button>
                  {/*&nbsp;*/}
                  {/*<button*/}
                  {/*    className="btn btn-primary"*/}
                  {/*    onClick={this.createItem2}*/}
                  {/*>*/}
                  {/*  Rank Paper*/}
                  {/*</button>*/}
                </div>
                {/*{this.renderTabList()}*/}
                <ul className="list-group list-group-flush border-top-0">
                  {this.renderItems()}
                </ul>
              </div>
            </div>
          </div>
          {this.state.modal ? (
              <Modal
                  activeItem={this.state.activeItem}
                  toggle={this.toggle}
                  onSave={this.handleSubmit}
              />
          ) : null}
        </main>
    );
  }
}

export default App;
